<template>
    <div class="test">
        <a href="#offcanvas-slide" class="uk-button uk-button-default uk-text-center" uk-toggle>Open</a>

        <div style=" margin-top: 64px;" id="offcanvas-slide" uk-offcanvas>

            <div class="uk-offcanvas-bar">


                <span>{{name}}</span>
                <img src="data:image/jpeg;base64," alt="">

               <div class="tests">
                   <h4><span class="tests" uk-icon="user"></span>&nbsp;&nbsp;$USERS$</h4>
                   <ul v-for="user in users" class="uk-nav uk-nav-default">
                       <div class="uk-width-auto">

                           <li class="uk-active"><img
                                   class="uk-border-circle"
                                   width="40"
                                   height="40"
                                   src="data:image/jpeg;base64,"
                       />{{user.name}}</li>
                       </div>

                   </ul>
               </div>

            </div>
        </div>
    </div>
</template>

<script>
    import {mapGetters} from 'vuex'
    import db from "@/firebase/init";
    export default {
        name: "sidebar",
        props:['name'],
        data(){
            return{
                users:[]
            }
        },
        computed:{
            ...mapGetters(['CurrentUser'])
        },
        created() {
            var observer = db.collection("message").onSnapshot(querySnapshot => {
                querySnapshot.docChanges().forEach(change => {
                    if (change.type === "added") {
                         this.users.push({name:change.doc.data().name})

                    }
                });
            });
        }

    }
</script>

<style scoped>
.test{
    margin-top: 80px;
    margin-left: 800px;

}
li{margin-top: 10px}
</style>
