<template>
  <div class="uk-container uk-container-small test vue-chat-scroll">
    <div
      v-for="message in messages"
      :key="message.message"
      class=" uk-card  uk-width-1-1@m"
    >
      <div class="uk-card-header">
        <div class="uk-grid-small uk-flex-middle rcorners" uk-grid>
          <div class="uk-width-auto">
            <img
              class="uk-border-circle"
              width="40"
              height="40"
              src="data:image/jpeg;base64,"
            />
          </div>
          <div class="uk-width-expand ">
            <div class="">
              <h3 class="uk-card-title uk-margin-remove-bottom">
                {{ message.name }}
              </h3>
              <p class="tests uk-text-meta uk-margin-remove-top">
                {{ message.time }}
              </p>
            </div>
            <p class="col">{{ message.message }}</p>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import db from "@/firebase/init";
import moments from 'moment'
export default {
  name: "SingleMessage",
  data() {
    return {
      messages: []
    };
  },
  methods: {},
  created() {
    var observer = db.collection("message").orderBy('time').onSnapshot(querySnapshot => {
      querySnapshot.docChanges().forEach(change => {
        if (change.type === "added") {
          this.messages.push({
            name: change.doc.data().name,
            message: change.doc.data().message,
            time: moments(change.doc.data().time).format('lll'),
            image:change.doc.data().image,
          });
        }
      });
    });
  }
};
</script>

